package book.commerce.test;

import java.util.Arrays;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.SpringApplication;
import org.springframework.boot.autoconfigure.SpringBootApplication;

import book.commerce.test.entity.Author;
import book.commerce.test.entity.Book;
import book.commerce.test.entity.User;
import book.commerce.test.repository.BookRepository;
import book.commerce.test.repository.UserRepository;

@SpringBootApplication
public class TestApplication {

	@Autowired
	private BookRepository bookRepository;

	@Autowired
	private UserRepository userRepository;

	public void test() {
		User dipta = new User("dipta", "dipta1102", "ROLE_ADMIN", null);
		this.userRepository.save(dipta);
		Author rowling = new Author("J.K.Rowling", "jk.rowling@gmail.com");
		Author stephen = Author.builder().authorName("Stephen King").authorEmail("sk@gmail.com").build();
		Book harryPotter = new Book("Harry Potter:The Sorcerer's Stone(book 1)", 780l, 1250.00, 120l,
				"https://m.media-amazon.com/images/I/81DI+BAN2SL._SY466_.jpg");
		Book theDarkTower2 = Book.builder().bookName("The Dark Tower 2(book 2)").pageCount(650l).price(900.00)
				.stock(40l)
				.bookImage(
						"data:image/jpeg;base64,")
				.build();
		Book wizardAndGlass = Book.builder().bookName("Wizard and Glass(book 3)").pageCount(780l).price(680.00)
				.stock(50l)
				.bookImage(
						"https://cdn.kobo.com/book-images/0d699c4b-63a0-4b93-bd99-11a5115576f5/353/569/90/False/the-dark-tower-iv-wizard-and-glass-1.jpg")
				.build();

		rowling.setBooks(Arrays.asList(harryPotter));
		stephen.setBooks(Arrays.asList(theDarkTower2, wizardAndGlass));

		harryPotter.setAuthors(Arrays.asList(rowling));
		theDarkTower2.setAuthors(Arrays.asList(stephen));
		wizardAndGlass.setAuthors(Arrays.asList(stephen));

		this.bookRepository.save(harryPotter);
		this.bookRepository.save(theDarkTower2);
		this.bookRepository.save(wizardAndGlass);
		System.out.println("----1 admin & three books with two author are saved----");

	}

	public static void main(String[] args) {
		SpringApplication.run(TestApplication.class, args);
		System.out.println("---Backend Server running Successfully---");
	}

}
